  /// Lấy danh sách từ theo cấp độ
  static List<Map<String, dynamic>> getWordsByLevel(String level) {
    return vocabularyData.where((word) => word['level'] == level).toList();
  }

  /// Lấy từ ngẫu nhiên cho luyện tập hàng ngày
  static List<Map<String, dynamic>> getDailyWords(int count) {
    final shuffled = List<Map<String, dynamic>>.from(vocabularyData)..shuffle();
    return shuffled.take(count).toList();
  }

  /// Lấy từ theo loại từ (noun, verb, adjective, etc.)
  static List<Map<String, dynamic>> getWordsByPartOfSpeech(String partOfSpeech) {
    return vocabularyData.where((word) => word['partOfSpeech'] == partOfSpeech).toList();
  }

  /// Lấy tổng số từ
  static int getTotalWordCount() {
    return vocabularyData.length;
  }

  /// Thống kê số lượng từ theo cấp độ
  static Map<String, int> getWordCountByLevel() {
    final counts = <String, int>{
      'A1': 1374,
      'A2': 895,
      'B1': 842,
      'B2': 367,
      'C1': 1255,
      'C2': 2024,
    };
    return counts;
  }

  /// Tìm kiếm từ theo keyword
  static List<Map<String, dynamic>> searchWords(String keyword) {
    final lowerKeyword = keyword.toLowerCase();
    return vocabularyData.where((word) {
      final wordText = (word['word'] as String).toLowerCase();
      final meaning = (word['meaning'] as String).toLowerCase();
      return wordText.contains(lowerKeyword) || meaning.contains(lowerKeyword);
    }).toList();
  }

  /// Lấy các từ trong một khoảng cấp độ
  static List<Map<String, dynamic>> getWordsInLevelRange(List<String> levels) {
    return vocabularyData.where((word) => levels.contains(word['level'])).toList();
  }
}
